def Name(genus):
	if flip_coin():
		return select1(Names(genus))
	O, N, C = Phonotactic(genus)
	o = select1(O)
	n = select1(N)
	c = select1(C)
	return f"{o}{n}{c}"

def Surname(genus):
	if flip_coin():
		ancestor = Name(genus)
		return f"{ancestor}us"
	P, F, S = Surphonotactic(genus)
	p = select1(P)
	f = select1(F)
	s = select1(S)
	return f"{p}{f}{s}us"

