def Phonotactic(Type):
	MALE    = "He"    in Type
	FEMALE  = "She"   in Type
	AGENDER = "They"  in Type

	prefix = [
		'Ad', "Alm", 'Am',
		'Bilb',	"Bel", "Bramb",	"Bals",	"Balsam",
		"Coc", "Carr",
		'El',
		"Frow",	'Frod',	"Fres", 'Frag',
		"Mac", 'Merr', 'Miel', "Moch",
		'Pip',
		'Vin', 'Viol'
		'Yem',
		"Zitr",
		]
	fix = ['',
		"amic", 'aret', "arul",
		"edon", 'endr', 'et',
		'ic', 'in',
		'on', 'ol', "ot",
		]
	suffix =  ["","","",
		'e','elbi',
		'i',
		'on','om',
		]
	if FEMALE:
		suffix = ['a','ia','','a']
	if MALE:
		suffix = ['o','io','','o']

	return prefix, fix, suffix

